mapper.select:
SELECT * FROM (SELECT ROWNUM RNUM, D_NUM, USER_ID, UNIQUE_NUM, D_WRITER, D_TITLE, D_CONTENT, D_FIND_DATE, D_FIND_LOCAL, D_GENDER, D_CATEGORY, D_LOCAL, D_PHONE, D_POINT, D_DATE, D_MDATE, D_COUNT, MAP_X, MAP_Y, D_CHECK, D_SUCCESS, VIEW_IMAGE, LIST_IMAGE FROM (SELECT * FROM DBOARD WHERE D_CHECK = 'y'
AND ${searchFiled} LIKE '%' || #{searchValue} || '%'
AND D_CATEGORY LIKE '%' || #{category} || '%'
AND D_LOCAL = #{local}
ORDER BY D_NUM DESC)) WHERE RNUM BETWEEN #{startRowNo} AND #{endRowNo}

SELECT * FROM (SELECT ROWNUM RNUM, D_NUM, USER_ID, UNIQUE_NUM, D_WRITER, D_TITLE, D_CONTENT, D_FIND_DATE, D_FIND_LOCAL, D_GENDER, D_CATEGORY, D_LOCAL, D_PHONE, D_POINT, D_DATE, D_MDATE, D_COUNT, MAP_X, MAP_Y, D_CHECK, D_SUCCESS, VIEW_IMAGE, LIST_IMAGE FROM (SELECT * FROM DBOARD WHERE 1=1
AND ${searchFiled} LIKE '%' || #{searchValue} || '%'
AND D_CATEGORY LIKE '%' || #{category} || '%'
AND D_LOCAL = #{local}
ORDER BY D_NUM DESC)) WHERE RNUM BETWEEN #{startRowNo} AND #{endRowNo}

SELECT NVL(MIN(D_NUM),#{dNum}) FROM DBOARD WHERE 1=1
AND ${searchFiled} LIKE '%' || #{searchValue} || '%'
AND D_CATEGORY LIKE '%' || #{ category } || '%'
AND D_LOCAL = #{ local }
AND D_CHECK = 'y' AND D_NUM > #{ dNum }

SELECT NVL(MIN(D_NUM),#{dNum}) FROM DBOARD WHERE 1=1
AND ${ searchFiled } LIKE '%' || #{ searchValue } || '%'
AND D_CATEGORY LIKE '%' || #{ category } || '%'
AND D_LOCAL = #{ local }
AND D_NUM > #{ dNum }

SELECT NVL(MAX(D_NUM),#{dNum}) FROM DBOARD WHERE 1=1
AND ${searchFiled} LIKE '%' || #{ searchValue } || '%'
AND D_CATEGORY LIKE '%' || #{ category } || '%'
AND D_LOCAL = #{ local }
AND D_CHECK = 'y' AND D_NUM < #{ dNum }

SELECT NVL(MAX(D_NUM),#{dNum}) FROM DBOARD WHERE 1=1
AND ${searchFiled} LIKE '%' || #{searchValue} || '%'
AND D_CATEGORY LIKE '%' || #{category} || '%'
AND D_LOCAL = #{local}
AND D_NUM < #{ dNum }

mapper.update:
UPDATE DBOARD SET
D_COUNT = D_COUNT + 1
WHERE D_NUM = #{ dNum }

mapper.delete:
DELETE 
FROM DBOARD 
WHERE D_NUM = #{ dNum }
